import {put, call, select} from "redux-saga/effects";
import {getFiles} from "../services/fileSystemService";
import {googleSignInInit, getRedirectUrl, authenticateUserIntoApp, authenticateUserIntoApp1} from "../services/authenticationService";
import {StartGoogleSignIn, GoogleInitSuccess, setRedirectUrl} from "../actions/actions";

export const authInstance = state => state.signInReducers.auth;

export const getRedirectUrlSaga = function*() {
    try {
        const res = yield call(getRedirectUrl);
        if(res.isAuthenticated)
            yield put(setRedirectUrl(res.home));
        else {
            window.location.href = res.home
        }
    }
    catch(e) {

    }
}

export const authenticateUserSaga = function*(params) {
    try {
        const res = yield call(authenticateUserIntoApp, params)
        const res1 = yield call(authenticateUserIntoApp1, params)
    }
    catch(e) {

    }
}

export const initGoogleSignIn = function*() {
    try {
        const res = yield call(googleSignInInit);
        yield put(GoogleInitSuccess(res()));
    }
    catch(err) {

    }
}

export const startGoogleSignIn = function*() {
    try {
        const auth = yield select(authInstance);
        console.log(auth);
        if(auth == null || auth == undefined) {
            //yield put({type: types.fa})
            return
        }
        yield auth.signIn();
    }
    catch(err) {

    }
}

export const getFilesSaga = function*() {
    try {
        const response = yield call(getFiles)
    }
    catch(err) {
    }
}
