import {put, call, select} from "redux-saga/effects";
import {getFiles} from "../services/fileSystemService";
import {googleSignInInit} from "../services/authenticationService";
import {StartGoogleSignIn, GoogleInitSuccess} from "../actions/actions";

const onAuthChange = (isSignedIn) => {
    if(isSignedIn) {
        console.log("loogedin")
    }
    else {
        console.log("not loogedin")
    }
}

export const authInstance = state => state.signInReducers.auth;

export const initGoogleSignIn = function*() {
    try {
        const res = yield call(googleSignInInit);
        yield put(GoogleInitSuccess(res()));
    }
    catch(err) {

    }
}

export const startGoogleSignIn = function*() {
    try {
        const auth = yield select(authInstance);
        console.log(auth);
        if(auth == null || auth == undefined) {
            //yield put({type: types.fa})
            return
        }
        auth.isSignedIn.listen(onAuthChange);
        yield auth.signIn();
    }
    catch(err) {

    }
}

export const getFilesSaga = function*() {
    try {
        const response = yield call(getFiles)
    }
    catch(err) {
    }
}
