import React from "react";
import {connect} from "react-redux";
import {Redirect} from "react-router-dom";
import {InitGoogleSignIn, StartGoogleSignIn} from "../../actions/actions"
import { Button } from "@material-ui/core";
import * as types from "../../actions/actionTypes";
import { urlParams } from "../../helpers";

class LoginPage extends React.PureComponent {

    componentDidMount() {
    }

    onGoogleSignInClicked = () => {
        //this.props.dispatch(StartGoogleSignIn());
        //this.props.auth.isSignedIn.listen(this.onAuthChange)
        this.props.auth.signIn();
    }

    render() {
        if(this.props.ok == "ok") {
            return <Redirect to="/files" />;
        }
        else {
            return (
                <div>

                    <Button onClick = {this.onGoogleSignInClicked}
                        variant = "contained" 
                        color = "secondary" >
                        Sign In with Google
                    </Button>
                </div>
            )
        }
    }
}

export const mapStateToProps = (state) => {
    return {
        auth: state.signInReducers.auth,
        isSignedIn: state.signInReducers.isSignedIn,
        ok: state.signInReducers.ok,
    }
}

export default connect(mapStateToProps, null)(LoginPage);
