import * as types from "../actions/actionTypes";

const INTIAL_STATE = {
    folders: [],
    loading: false,
};

export const filesDashboardReducers = (state = INTIAL_STATE, action) => {
    switch(action.type) {
        case types.GET_FILES:
            return Object.assign({}, state, {
                loading: true,
              })
        default:
            return state;
    }
}
